import React from "react";

const TextInput = ({ id, displayName, isRequired, handleChange, display}) => {
	if( display === true){
		if(id === "name"){
		return(
			<div className="form-group">
	      		<label className = "form-label">
	        		{displayName}
	      		</label>
	      		<input
			        className = "form-input"
			        type = "text"
			        pattern="(?=^.{0,40}$)^[a-zA-Z-]+\s[a-zA-Z-]+$"
			        id = {id}
			        name = {displayName}
			        required = {isRequired}

			        onChange = {handleChange}
		        />
		    </div>
			);
		}else{
		return(
			<div className="form-group">
	      		<label className = "form-label">
	        		{displayName}
	      		</label>
	      		<input
			        className = "form-input"
			        type = "text"
			        id = {id}
			        name = {displayName}
			        required = {isRequired}

			        onChange = {handleChange}
		        />
		    </div>
			);
		}
	}else{
		return(<input type="hidden"/>);
	}


};

export default TextInput;
