import React from "react";

const DropdownSelect = ({ id, name, onChange, required, options, display}) =>{

	let defaultArray = options.filter(option=>{
		if(option.isDefault ===  true)
			return option.name
	});
	let defaultObj = {...defaultArray};
	console.log(defaultArray)

	if( display === true){
		return (
		<div className="form-group">
			<label className = "form-content" key = {id+'i'} htmlFor = {id}>
				{name}
			</label>
			<select 
				className="form-input" 
				id={id} 
				required = {required} 
				onChange = {onChange} 

			>

				{options.map(option=>{
					return (
						<option 
							key = {option.id} 
							value = {option.name}						
						>
							{option.name}
						</option>
					);

				})}

			</select>
		</div>
		);
	}else{
		return(<input type="hidden"/>);
	}

};

export default DropdownSelect;
